package com.kucaroom.mypicture.domain;import lombok.Data;import javax.persistence.*;@Data@Entity@Table(name = "about_us")public class AboutUs {    @Id    @GeneratedValue(strategy= GenerationType.IDENTITY)    private Integer id;    private Integer appId;    /** logo */    private String logo;    /** 文字描述 */    private String description;    /** 公众号 */    private String publicNumber;    /** 微信号 */    private String WeChatNumber;    /** 手机号 */    private String phone;    /** 邮箱 */    private String email;    /** 二维码 */    private String qrCode;    private String createAt;}