package com.kucaroom.mypicture.domain;import lombok.Data;import javax.persistence.*;@Data@Entity@Table(name = "pictures")public class Picture {    @Id    @GeneratedValue(strategy = GenerationType.IDENTITY)    private Integer id;    /** 所属小程序 */    private Integer appId;    /** 用户 */    private Integer UserId;    /** 图集标题 */    private String title;    /** 图集内容 */    private String content;    /** 图集头像 */    private String avatar;    /** 封面图片 */    private String coverPicture;    /** 浏览人数 */    private Integer viewNum;    /** 下载封面人数 */    private Integer downLoadNum;    /** 分享人数 */    private Integer shareNum;    /** 类型 */    private Integer type;    /** 图集状态，1=下架，2=上架 */    private Integer status;    private String createAt;}