package com.kucaroom.mypicture.domain;import lombok.Data;import javax.persistence.*;@Data@Entity@Table(name = "picture_items")public class PictureItem {    @Id    @GeneratedValue(strategy = GenerationType.IDENTITY)    private Integer id;    /** 所属小程序 */    private Integer appId;    /** 所属图集 */    private Integer pictureId;    /** 标题 */    private String title;    /** 内容 */    private String content;    /** 图片 */    private String picture;    /** 浏览人数 */    private Integer viewNum;    /** 下载封面人数 */    private Integer downLoadNum;    /** 分享人数 */    private Integer shareNum;    /** 类型 */    private Integer type;    /** 状态*/    private Integer status;    private String createAt;}