package com.kucaroom.mypicture.domain;import lombok.Data;import javax.persistence.*;@Data@Entity@Table(name = "qiniu_accounts")public class QiNiuAcctount {    @Id    @GeneratedValue(strategy = GenerationType.IDENTITY)    private Integer id;    /** 所属小程序 */    private Integer appId;    /** 七牛key */    private String accessKey;    /** 密钥 */    private String secretKey;    /** 存储桶 */    private String bucketName;    /** 获取token地址 */    private String url = "";    /** 域名 */    private String domain = "";    /** 七牛token */    private String token;    /** 七牛存储区域 */    private String region;    /** 是否是默认的七牛账号，1=是，2=不是 */    private Integer status = 1;    /** 过期时间 默认是一天 */    private String expireAt;    private String createAt;}