package com.kucaroom.mypicture.domain;import lombok.Data;import javax.persistence.Entity;import javax.persistence.GeneratedValue;import javax.persistence.GenerationType;import javax.persistence.Id;import java.util.Date;@Data@Entitypublic class User {    @Id    @GeneratedValue(strategy= GenerationType.IDENTITY)    private Integer id;    /** 所属的小程序 */    private Integer appId;    /** 昵称 */    private String nickname;    /** 密码 */    private String password;    /** 手机号码 */    private String mobile;    /** 头像 */    private String avatar;    /** 性别 */    private Integer gender;    /** 微信open id */    private String openId;    /** 微信union ID */    private String unionId;    /** 城市 */    private String city;    /** 国家 */    private String country;    /** 语言 */    private String language;    /** 省份 */    private String province;    /** 类型 */    private Integer type = 0;    /** 状态 */    private Integer status = 0;    private String createAt;    private Date deletedAt;}