package com.kucaroom.mypicture.domain;import lombok.Data;import javax.persistence.*;import java.util.Date;@Data@Entity@Table(name = "we_chat_app")public class WeChatApp {    @Id    @GeneratedValue(strategy= GenerationType.IDENTITY)    private Integer id;    /** 小程序的名字 */    private String name;    /** 小程序的 app_key */    private String appKey;    /** 小程序的 app_secret */    private String appSecret;    /** 后台与前端对接的 alliance_key */    private String allianceKey;    /** 小程序域名 */    private String domain;    /** 小程序logo */    private String logo = "";    /** 小程序二维码 */    private String qrCode = "";    /** 小程序分享的封面图 */    private String shareImage = "";    private String shareWord = "";    /** 管理员 */    private Integer adminId;    /** 小程序的状态 1=待审核，2=正常，3=关闭 */    private Integer status;    private Date deletedAt;}