package com.kucaroom.mypicture.aspect;import com.kucaroom.mypicture.enums.ResponseEnum;import com.kucaroom.mypicture.exception.WebApiException;import com.kucaroom.mypicture.service.AuthService;import lombok.extern.slf4j.Slf4j;import org.apache.shiro.SecurityUtils;import org.aspectj.lang.annotation.*;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Component;import org.springframework.util.StringUtils;import org.springframework.web.context.request.RequestContextHolder;import org.springframework.web.context.request.ServletRequestAttributes;import javax.servlet.http.HttpServletRequest;@Slf4j@Aspect@Componentpublic class AuthAspect {    @Autowired    private AuthService authService;    /**     * 拦截具体控制器下的所有方法     */    @Pointcut("execution(public * com.kucaroom.mypicture.controller.AdminController.*(..))")    public void init(){    }    @Before("init()")    public void auth(){        String email = (String) SecurityUtils.getSubject().getPrincipal();        if(email == null){            log.info("用户：{}",email);            throw new WebApiException(ResponseEnum.ADMIN_NOT_LOGIN);        }    }    @After("init()")    public void response(){    }    /**     * 获取返回的数据对象     * @param object     */    @AfterReturning(returning = "object",pointcut = "init()")    public void responseData(Object object){    }}