package com.kucaroom.mypicture.controller;import lombok.extern.slf4j.Slf4j;import org.apache.shiro.SecurityUtils;import org.springframework.stereotype.Controller;import org.springframework.web.bind.annotation.GetMapping;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.servlet.ModelAndView;@Slf4j@Controller@RequestMapping("/admin")public class AdminController {    @GetMapping("")    public ModelAndView index(){        log.info("登录的用户：{}",SecurityUtils.getSubject().getPrincipal());        return new ModelAndView("/admin/index");    }    @GetMapping("/dashboard")    public ModelAndView dashboard(){        return new ModelAndView("/admin/dashboard");    }}