package com.kucaroom.mypicture.controller;import com.kucaroom.mypicture.DTO.AdminDTO;import com.kucaroom.mypicture.service.AdminService;import org.apache.shiro.SecurityUtils;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Controller;import org.springframework.util.StringUtils;import org.springframework.web.bind.annotation.GetMapping;import org.springframework.web.servlet.ModelAndView;import java.util.HashMap;import java.util.Map;@Controllerpublic class IndexController {    @Autowired    private AdminService adminService;    @GetMapping("/")    public ModelAndView index(){        String email = (String) SecurityUtils.getSubject().getPrincipal();        Map<String,Object> map = new HashMap<>();        if(!StringUtils.isEmpty(email)){            AdminDTO adminDTO = adminService.findByEmail(email);            map.put("username",adminDTO.getUsername());        }else{            map.put("username","");        }        return new ModelAndView("home/index",map);    }}