package com.kucaroom.mypicture.controller.checkAuth;import com.kucaroom.mypicture.DTO.QiNiuAccountDTO;import com.kucaroom.mypicture.service.QiNiuService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Controller;import org.springframework.web.bind.annotation.GetMapping;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.servlet.ModelAndView;import javax.servlet.http.HttpServletRequest;import java.util.HashMap;import java.util.Map;@Controller@RequestMapping("/admin")public class SettingController {    @Autowired    private QiNiuService qiNiuService;    @GetMapping("/setting")    public ModelAndView settingView(HttpServletRequest request){        Integer appId = (Integer) request.getAttribute("appId");        QiNiuAccountDTO qiNiuAccountDTO = qiNiuService.findAccountByAppId(appId);        Map<String,Object> map = new HashMap<>();        map.put("domain",qiNiuAccountDTO.getDomain());        map.put("uploadDomain",qiNiuAccountDTO.getUrl());        return new ModelAndView("/admin/setting",map);    }}