package com.kucaroom.mypicture.converter;import com.kucaroom.mypicture.DTO.AdminDTO;import com.kucaroom.mypicture.form.AdminForm;public class AdminFormToAdminDTO {    /**     * orderForm转换成orderDTO     * @param adminForm     * @return     */    public static AdminDTO convert(AdminForm adminForm){        AdminDTO adminDTO = new AdminDTO();        adminDTO.setUsername(adminForm.getUsername());        adminDTO.setEmail(adminForm.getEmail());        adminDTO.setPassword(adminForm.getPassword());        return adminDTO;    }}