package com.kucaroom.mypicture.converter;import com.kucaroom.mypicture.DTO.PictureDTO;import com.kucaroom.mypicture.domain.PictureItem;import com.kucaroom.mypicture.responseObject.PictureItemRes;import com.kucaroom.mypicture.responseObject.PictureRes;import org.springframework.beans.BeanUtils;import java.util.ArrayList;import java.util.List;public class PictureDTOPictureRes {    public static PictureRes convert(PictureDTO pictureDTO){        PictureRes pictureRes = new PictureRes();        BeanUtils.copyProperties(pictureDTO,pictureRes);        List<PictureItemRes> pictureResList = new ArrayList<>();        for (PictureItem pictureItem:pictureDTO.getPictureItems()){            PictureItemRes pictureResItem = new PictureItemRes();            BeanUtils.copyProperties(pictureItem,pictureResItem);            pictureResList.add(pictureResItem);        }        pictureRes.setPictureList(pictureResList);        return pictureRes;    }}