package com.kucaroom.mypicture.converter;import com.kucaroom.mypicture.DTO.PictureDTO;import com.kucaroom.mypicture.domain.CollectPictureLog;import com.kucaroom.mypicture.domain.PictureItem;import com.kucaroom.mypicture.responseObject.PictureItemRes;import com.kucaroom.mypicture.responseObject.PictureRes;import org.springframework.beans.BeanUtils;import java.text.ParseException;import java.text.SimpleDateFormat;import java.util.ArrayList;import java.util.HashMap;import java.util.List;import java.util.Map;import java.util.stream.Collectors;public class PictureDTOPictureRes {    /**     * 转化     *     * @author yezi     * @param pictureDTO     * @return     */    public static PictureRes convert(PictureDTO pictureDTO){        PictureRes pictureRO = new PictureRes();        BeanUtils.copyProperties(pictureDTO,pictureRO);        return  pictureRO;    }    /**     * 循环转化     *     * @author yezi     * @param pictureDTOList     * @return     */    public static List<PictureRes> convert(List<PictureDTO> pictureDTOList){        return pictureDTOList.stream().map(                e -> convert(e)        ).collect(Collectors.toList());    }}