package com.kucaroom.mypicture.converter;import com.kucaroom.mypicture.domain.PictureItem;import com.kucaroom.mypicture.responseObject.PictureItemRes;import org.springframework.beans.BeanUtils;import java.util.List;import java.util.stream.Collectors;public class PictureItemToRes {    /**     * 转化     *     * @author yezi     * @param pictureItem     * @return     */    public static PictureItemRes convert(PictureItem pictureItem){        PictureItemRes pictureRO = new PictureItemRes();        BeanUtils.copyProperties(pictureItem,pictureRO);        return  pictureRO;    }    /**     * 循环转化     *     * @author yezi     * @param pictureItemList     * @return     */    public static List<PictureItemRes> convert(List<PictureItem> pictureItemList){        return pictureItemList.stream().map(                e -> convert(e)        ).collect(Collectors.toList());    }}