package com.kucaroom.mypicture.converter;import com.kucaroom.mypicture.DTO.PictureDTO;import com.kucaroom.mypicture.DTO.UserDTO;import com.kucaroom.mypicture.domain.Picture;import com.kucaroom.mypicture.domain.User;import org.springframework.beans.BeanUtils;import java.util.List;import java.util.stream.Collectors;public class UserToUserDTO {    /**     * 转化     *     * @author yezi     * @param user     * @return     */    public static UserDTO convert(User user){        UserDTO userDTO = new UserDTO();        BeanUtils.copyProperties(user,userDTO);        return  userDTO;    }    /**     * 循环转化     *     * @author yezi     * @param pictureList     * @return     */    public static List<UserDTO> convert(List<User> pictureList){        return pictureList.stream().map(                e -> convert(e)        ).collect(Collectors.toList());    }}