package com.kucaroom.mypicture.exception;import com.kucaroom.mypicture.enums.ResponseEnum;import lombok.Data;@Datapublic class WebApiException extends RuntimeException {    private Integer code;    private String message;    public WebApiException(Integer code,String message){        super(message);        this.code = code;        this.message = message;    }    public WebApiException(ResponseEnum responseEnum){        super(responseEnum.getMessage());        this.code = responseEnum.getCode();        this.message = responseEnum.getMessage();    }    @Override    public String toString() {        return "ApiException{" +                "code=" + code +                ", message='" + message + '\'' +                '}';    }}