package com.kucaroom.mypicture.exception;import com.kucaroom.mypicture.enums.ResponseEnum;import lombok.Data;import lombok.extern.slf4j.Slf4j;@Data@Slf4jpublic class WebApiException extends RuntimeException {    private Integer code;    private String message;    public WebApiException(Integer code,String message){        super(message);        this.code = code;        this.message = message;    }    public WebApiException(ResponseEnum responseEnum){        log.info("异常信息：{}",responseEnum.getMessage());        this.code = responseEnum.getCode();        this.message = responseEnum.getMessage();    }    @Override    public String toString() {        return "ApiException{" +                "code=" + code +                ", message='" + message + '\'' +                '}';    }}