package com.kucaroom.mypicture.form;import lombok.Data;import org.hibernate.validator.constraints.Length;import javax.validation.constraints.Email;import javax.validation.constraints.NotEmpty;/** * 后台用户注册表单 */@Datapublic class AdminForm {    @NotEmpty(message = "用户名不能为空！")    private String username;    @NotEmpty(message = "邮箱不能为空！")    @Email(message = "邮箱格式不正确！")    private String email;    @NotEmpty(message = "密码不能为空！")    @Length(min = 6,max = 16,message = "密码长度为6-16之间！")    private String password;    @NotEmpty(message = "确认密码不能为空")    private String passwordConfirmation;}