package com.kucaroom.mypicture.mapper;import org.apache.ibatis.annotations.*;public interface CollectPictureLogMapper {    @Delete("delete from collect_picture_log where picture_item_id = #{pictureItemId,jdbcType=INTEGER}")    Integer deleteByPictureItemId(@Param("pictureItemId") Integer pictureItemId);    @Select("delete from collect_picture_log where picture_item_id in (${pictureItemIds})")    Integer batchDelete(@Param("pictureItemIds")String pictureItemIds);}