package com.kucaroom.mypicture.mapper;import org.apache.ibatis.annotations.Delete;import org.apache.ibatis.annotations.Param;public interface DownloadPictureLogMapper {    @Delete("delete from download_picture_log where picture_item_id = #{pId,jdbcType=INTEGER} and type = #{type,jdbcType=INTEGER}")    Integer deleteByPictureItemIdAndType(@Param("pId") Integer pId, @Param("type") Integer type);}