package com.kucaroom.mypicture.mapper;import org.apache.ibatis.annotations.Delete;import org.apache.ibatis.annotations.Param;import org.apache.ibatis.annotations.Select;public interface PictureItemMapper {    @Delete("delete from picture_items where id = #{id,jdbcType=INTEGER}")    Integer deleteById(@Param("id")Integer id);    @Delete("delete from picture_items where picture_id = #{pictureId,jdbcType=INTEGER}")    Integer deleteByPictureId(@Param("pictureId")Integer id);}