package com.kucaroom.mypicture.mapper;import com.kucaroom.mypicture.domain.Picture;import org.apache.ibatis.annotations.Delete;import org.apache.ibatis.annotations.Param;public interface PictureMapper {    @Delete("delete from pictures where id = #{id,jdbcType=INTEGER} and app_id = #{appId,jdbcType=INTEGER}")    Integer deleteByIdAndAppId(@Param("id")Integer id,@Param("appId")Integer appId);}