package com.kucaroom.mypicture.mapper;import org.apache.ibatis.annotations.Delete;import org.apache.ibatis.annotations.Param;public interface ViewPictureLogMapper {    @Delete("delete from view_picture_log where picture_id = #{pictureId,jdbcType=INTEGER} and type = #{type,jdbcType=INTEGER}")    Integer deleteByPictureIdAndType(@Param("pictureId")Integer pictureId,@Param("type")Integer type);}