package com.kucaroom.mypicture.service.impl;import com.kucaroom.mypicture.DTO.AboutUsDTO;import com.kucaroom.mypicture.domain.AboutUs;import com.kucaroom.mypicture.enums.ResponseEnum;import com.kucaroom.mypicture.exception.WebApiException;import com.kucaroom.mypicture.repository.AboutUsRepository;import com.kucaroom.mypicture.service.AboutUsService;import org.springframework.beans.BeanUtils;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import java.util.Optional;@Servicepublic class AboutUsServiceImpl implements AboutUsService {    @Autowired    private AboutUsRepository aboutUsRepository;    @Override    public AboutUsDTO create(AboutUsDTO aboutUsDTO){        AboutUsDTO aboutUs = this.findByAppId(aboutUsDTO.getAppId());        if(aboutUs != null){            throw new WebApiException(ResponseEnum.ABOUT_US_EXIST);        }        AboutUs aboutUsModel = new AboutUs();        BeanUtils.copyProperties(aboutUsDTO,aboutUsModel);        AboutUs result = aboutUsRepository.save(aboutUsModel);        aboutUsDTO.setId(result.getId());        return aboutUsDTO;    }    @Override    public AboutUsDTO findByAppId(Integer appId) {        AboutUs aboutUs = aboutUsRepository.findByAppId(appId);        if(aboutUs == null){            return null;        }        AboutUsDTO aboutUsDTO = new AboutUsDTO();        BeanUtils.copyProperties(aboutUs,aboutUsDTO);        return aboutUsDTO;    }    @Override    public AboutUsDTO updateByAppId(AboutUsDTO aboutUsDTOData) {        AboutUsDTO aboutUsDTO = this.findByAppId(aboutUsDTOData.getAppId());        if(aboutUsDTO == null){            throw new WebApiException(ResponseEnum.ABOUT_US_NOT_EXIST);        }        aboutUsDTOData.setId(aboutUsDTO.getId());        AboutUs aboutUs = new AboutUs();        BeanUtils.copyProperties(aboutUsDTOData,aboutUs);        AboutUs result = aboutUsRepository.save(aboutUs);        BeanUtils.copyProperties(result,aboutUsDTOData);        return aboutUsDTOData;    }}