package com.kucaroom.mypicture.service.impl;import com.kucaroom.mypicture.mapper.CollectPictureLogMapper;import com.kucaroom.mypicture.service.CollectPictureLogService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;@Servicepublic class CollectPictureLogServiceImpl implements CollectPictureLogService {    @Autowired    private CollectPictureLogMapper collectPictureLogMapper;    @Override    public Integer deleteByPictureItemId(Integer pictureItemId) {        Integer result = collectPictureLogMapper.deleteByPictureItemId(pictureItemId);        return result;    }    @Override    public Integer batchDelete(String pictureItemIds) {        Integer result = collectPictureLogMapper.batchDelete(pictureItemIds);        return result;    }}