package com.kucaroom.mypicture.service.impl;import com.kucaroom.mypicture.mapper.DownloadPictureLogMapper;import com.kucaroom.mypicture.service.DownloadPictureService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;@Servicepublic class DownloadPictureServiceImpl implements DownloadPictureService {    @Autowired    private DownloadPictureLogMapper downloadPictureLogMapper;    @Override    public Integer deleteByPictureItemIdAndType(Integer pId, Integer type) {        Integer result = downloadPictureLogMapper.deleteByPictureItemIdAndType(pId,type);        return result;    }}