package com.kucaroom.mypicture.service.impl;import com.kucaroom.mypicture.mapper.ViewPictureLogMapper;import com.kucaroom.mypicture.service.ViewPictureLogService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;@Servicepublic class ViewPictureLogServiceImpl implements ViewPictureLogService {    @Autowired    private ViewPictureLogMapper viewPictureLogMapper;    @Override    public Integer deleteByPictureIdAndType(Integer pictureId, Integer type) {        Integer result = viewPictureLogMapper.deleteByPictureIdAndType(pictureId,type);        return result;    }}