package com.kucaroom.mypicture.service.impl;import com.kucaroom.mypicture.DTO.WeChatAppDTO;import com.kucaroom.mypicture.domain.WeChatApp;import com.kucaroom.mypicture.repository.WeChatAppRepository;import com.kucaroom.mypicture.service.WeChatAppService;import org.springframework.beans.BeanUtils;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;@Servicepublic class WeChatAppServiceImpl implements WeChatAppService {    @Autowired    private WeChatAppRepository weChatAppRepository;    /**     * 根据管理员查询小程序的信息     *     * @author yezi     * @param adminId     * @return     */    @Override    public WeChatAppDTO findByAdminId(Integer adminId) {        WeChatApp weChatApp = weChatAppRepository.findByAdminId(adminId);        if(weChatApp != null){            WeChatAppDTO weChatAppDTO = new WeChatAppDTO();            BeanUtils.copyProperties(weChatApp,weChatAppDTO);            return weChatAppDTO;        }        return null;    }}