package com.kucaroom.mypicture.repository;import com.kucaroom.mypicture.domain.Admin;import com.kucaroom.mypicture.util.KeyUtil;import com.kucaroom.mypicture.util.MD5Util;import lombok.extern.slf4j.Slf4j;import org.apache.shiro.crypto.hash.Md5Hash;import org.junit.Assert;import org.junit.Test;import org.junit.runner.RunWith;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.boot.test.context.SpringBootTest;import org.springframework.test.context.junit4.SpringRunner;@Slf4j@SpringBootTest@RunWith(SpringRunner.class)public class AdminRepositoryTest {    @Autowired    private AdminRepository adminRepository;    @Test    public void create(){        String salt = KeyUtil.generateKey(6);        Admin admin = new Admin();        admin.setUsername("区叶子");        admin.setEmail("875307054@aa.com");        admin.setAvatar("http:baidu.com");        admin.setPassword(MD5Util.encrypt("bingbing",salt));        admin.setSalt(salt);        admin.setMobile("13425144866");        admin.setActiveToken(salt);        admin.setStatus(0);        Admin saveResult = adminRepository.save(admin);        Assert.assertEquals(admin.getEmail(),saveResult.getEmail());    }    @Test    public void encrypt(){        String salt = KeyUtil.generateKey(6);        Md5Hash password = new Md5Hash("bingbing");        log.info("盐值：{}",salt);        log.info("密文：{}",password.toString());    }    @Test    public void findByEmail(){        Admin admin = adminRepository.findByEmail("875307054@qq.com");        log.info("密码：{}",admin.getPassword());        Assert.assertNotNull(admin);    }    @Test    public void Random(){        log.info("随机字符串："+KeyUtil.generateKey(6));    }}