package com.kucaroom.mypicture.service.impl;import com.kucaroom.mypicture.service.AuthService;import lombok.extern.slf4j.Slf4j;import org.apache.shiro.SecurityUtils;import org.apache.shiro.subject.Subject;import org.junit.Assert;import org.junit.Test;import org.junit.runner.RunWith;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.boot.test.context.SpringBootTest;import org.springframework.test.context.junit4.SpringRunner;import static org.junit.Assert.*;@Slf4j@RunWith(SpringRunner.class)@SpringBootTestpublic class AuthServiceImplTest {    @Autowired    private AuthService authService;    @Test    public void login() {        Boolean loginResult = authService.login("875307054@aa.com","bingbing");        Assert.assertEquals(true,loginResult);    }    @Test    public void logout() {        Boolean loginResult = authService.login("875307054@aa.com","bingbing");        Assert.assertEquals(true,loginResult);        authService.logout();        Boolean logout = authService.isAuthenticated();        Assert.assertEquals(false,logout);    }    @Test    public void isAuthenticated() {        Boolean loginResult = authService.login("875307054@aa.com","bingbing");        Assert.assertEquals(true,loginResult);        Boolean auth = authService.isAuthenticated();        Assert.assertEquals(true,auth);    }    @Test    public void loginUser() {        Boolean loginResult = authService.login("875307054@aa.com","bingbing");        Assert.assertEquals(true,loginResult);        String email = authService.loginUser();        log.info("登录用户的email:{}",email);        Assert.assertEquals(email,"875307054@aa.com");    }}