package com.kucaroom.mypicture.service.impl;import com.kucaroom.mypicture.DTO.UserDTO;import com.kucaroom.mypicture.service.UserService;import lombok.extern.slf4j.Slf4j;import org.junit.Assert;import org.junit.Test;import org.junit.runner.RunWith;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.boot.test.context.SpringBootTest;import org.springframework.data.domain.Page;import org.springframework.data.domain.PageRequest;import org.springframework.data.domain.Sort;import org.springframework.test.context.junit4.SpringRunner;@Slf4j@SpringBootTest@RunWith(SpringRunner.class)public class UserServiceImplTest {    @Autowired    private UserService userService;    @Test    public void users() {        Sort sort = new Sort(Sort.Direction.DESC, "createAt");        PageRequest pageable = new PageRequest(0,10,sort);        Integer appId = 74;        Page<UserDTO> userDTOS = userService.users(appId,pageable);        log.info("结果：{}",userDTOS.getContent().toString());        Assert.assertNotNull(userDTOS.getContent());    }}