package com.kucaroom.mypicture.service.impl;import com.kucaroom.mypicture.DTO.WeChatAppDTO;import com.kucaroom.mypicture.service.WeChatAppService;import lombok.extern.slf4j.Slf4j;import org.junit.Assert;import org.junit.Test;import org.junit.runner.RunWith;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.boot.test.context.SpringBootTest;import org.springframework.test.context.junit4.SpringRunner;@Slf4j@SpringBootTest@RunWith(SpringRunner.class)public class WeChatAppServiceImplTest {    @Autowired    private WeChatAppService weChatAppService;    @Test    public void edit() {        Integer appId = 74;        String key = "name";        String value = "唯美";        WeChatAppDTO weChatAppDTO = weChatAppService.edit(appId,key,value);        log.info("内容：{}",weChatAppDTO.toString());        Assert.assertNotNull(weChatAppDTO);        Assert.assertEquals(value,weChatAppDTO.getName());    }}