package com.kucaroom.mypicture.controller;import com.kucaroom.mypicture.DTO.UserDTO;import com.kucaroom.mypicture.form.LoginForm;import com.kucaroom.mypicture.responseObject.UserRO;import com.kucaroom.mypicture.service.UserService;import com.kucaroom.mypicture.util.ResponseData;import com.kucaroom.mypicture.util.ResponseUtil;import lombok.extern.slf4j.Slf4j;import org.springframework.beans.BeanUtils;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.web.bind.annotation.*;@RestController@RequestMapping("/auth")@Slf4jpublic class AuthController {    @Autowired    private UserService userService;    /**     * 微信登录小程序     *     * @param loginForm     * @return     */    @PostMapping("/login")    public ResponseData login(@RequestBody LoginForm loginForm){        UserDTO result = userService.userLogin(loginForm);        UserRO userRO = new UserRO();        BeanUtils.copyProperties(result,userRO);        return ResponseUtil.success(userRO);    }}