package com.kucaroom.mypicture.controller;import com.kucaroom.mypicture.DTO.UserDTO;import com.kucaroom.mypicture.responseObject.UserRO;import com.kucaroom.mypicture.service.UserService;import com.kucaroom.mypicture.util.ResponseData;import com.kucaroom.mypicture.util.ResponseUtil;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.web.bind.annotation.GetMapping;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RestController;@RequestMapping("/user")@RestControllerpublic class UserController {    @Autowired    private UserService userService;    @GetMapping("/profile")    public ResponseData userInfo(){        String openid = "";        UserDTO userDTO = userService.findUserByOpenId(openid);        UserRO userResponseData = new UserRO();        userResponseData.setAppId(userDTO.getAppId());        userResponseData.setId(userDTO.getId());        userResponseData.setNickname(userDTO.getNickname());        userResponseData.setAvatar(userDTO.getAvatar());        userResponseData.setGender(userDTO.getGender());        return ResponseUtil.success(userResponseData);    }}