package com.kucaroom.mypicture.convert;import com.kucaroom.mypicture.domain.DownLoadPictureLog;import com.kucaroom.mypicture.domain.ViewPictureLog;import com.kucaroom.mypicture.responseObject.DownloadPictureRO;import com.kucaroom.mypicture.responseObject.ViewPictureLogRO;import java.util.List;import java.util.stream.Collectors;public class DownloadPictureLogToRO {    /**     * 转化     *     * @author yezi     * @param downLoadPictureLog     * @return     */    public static DownloadPictureRO convert(DownLoadPictureLog downLoadPictureLog){        DownloadPictureRO downloadPictureRO = new DownloadPictureRO();        downloadPictureRO.setId(downLoadPictureLog.getId());        downloadPictureRO.setPictureItemId(downLoadPictureLog.getPictureItemId());        downloadPictureRO.setType(downLoadPictureLog.getType());        downloadPictureRO.setUserId(downLoadPictureLog.getUserId());        return  downloadPictureRO;    }    /**     * 循环转化     *     * @author yezi     * @param downLoadPictureLogs     * @return     */    public static List<DownloadPictureRO> convert(List<DownLoadPictureLog> downLoadPictureLogs){        return downLoadPictureLogs.stream().map(                e -> convert(e)        ).collect(Collectors.toList());    }}