package com.kucaroom.mypicture.convert;import com.kucaroom.mypicture.DTO.PictureDTO;import com.kucaroom.mypicture.domain.Picture;import com.kucaroom.mypicture.responseObject.PictureRO;import org.springframework.beans.BeanUtils;import java.util.List;import java.util.stream.Collectors;public class PictureDTOPictureRO {    /**     * 转化     *     * @author yezi     * @param pictureDTO     * @return     */    public static PictureRO convert(PictureDTO pictureDTO){        PictureRO pictureRO = new PictureRO();        BeanUtils.copyProperties(pictureDTO,pictureRO);        pictureRO.setPictureItems(PictureItemToPictureItemRO.convert(pictureDTO.getPictureItems()));        return  pictureRO;    }    /**     * 循环转化     *     * @author yezi     * @param pictureDTOList     * @return     */    public static List<PictureRO> convert(List<PictureDTO> pictureDTOList){        return pictureDTOList.stream().map(                e -> convert(e)        ).collect(Collectors.toList());    }}