package com.kucaroom.mypicture.convert;import com.kucaroom.mypicture.DTO.PictureDTO;import com.kucaroom.mypicture.domain.Picture;import org.springframework.beans.BeanUtils;import java.util.List;import java.util.stream.Collectors;public class PictureToPictureDTO {    /**     * 转化     *     * @author yezi     * @param picture     * @return     */    public static PictureDTO convert(Picture picture){        PictureDTO pictureDTO = new PictureDTO();        BeanUtils.copyProperties(picture,pictureDTO);        return  pictureDTO;    }    /**     * 循环转化     *     * @author yezi     * @param pictureList     * @return     */    public static List<PictureDTO> convert(List<Picture> pictureList){        return pictureList.stream().map(                e -> convert(e)        ).collect(Collectors.toList());    }}