package com.kucaroom.mypicture.convert;import com.kucaroom.mypicture.domain.ViewPictureLog;import com.kucaroom.mypicture.responseObject.ViewPictureLogRO;import java.util.HashMap;import java.util.List;import java.util.Map;import java.util.stream.Collectors;public class ViewPictureLogToRO {    /**     * 转化     *     * @author yezi     * @param viewPictureLog     * @return     */    public static ViewPictureLogRO convert(ViewPictureLog viewPictureLog){        ViewPictureLogRO viewPictureLogRO = new ViewPictureLogRO();        viewPictureLogRO.setId(viewPictureLog.getId());        viewPictureLogRO.setPictureId(viewPictureLog.getPictureId());        viewPictureLogRO.setType(viewPictureLog.getType());        viewPictureLogRO.setUserId(viewPictureLog.getUserId());        return  viewPictureLogRO;    }    /**     * 循环转化     *     * @author yezi     * @param viewPictureLogs     * @return     */    public static List<ViewPictureLogRO> convert(List<ViewPictureLog> viewPictureLogs){        List<ViewPictureLogRO> list = viewPictureLogs.stream().map(                e -> convert(e)        ).collect(Collectors.toList());        return list;    }}