package com.kucaroom.mypicture.exception;import com.kucaroom.mypicture.enums.ResponseEnum;import lombok.Data;import lombok.extern.slf4j.Slf4j;/** * 自定义异常类 */@Data@Slf4jpublic class ApiException extends RuntimeException {    private Integer code;    private String message;    public ApiException(Integer code,String message){        super(message);        this.code = code;        this.message = message;    }    public ApiException(ResponseEnum responseEnum){        super(responseEnum.getMessage());        this.code = responseEnum.getCode();        this.message = responseEnum.getMessage();    }    @Override    public String toString() {        return "ApiException{" +                "code=" + code +                ", message='" + message + '\'' +                '}';    }}