package com.kucaroom.mypicture.mapper;import com.kucaroom.mypicture.domain.CollectPictureLog;import org.apache.ibatis.annotations.Param;import org.apache.ibatis.annotations.Result;import org.apache.ibatis.annotations.Results;import org.apache.ibatis.annotations.Select;import java.util.List;public interface CollectPictureMapper {    @Select("select id,user_id,picture_item_id " +            "from collect_picture_log " +            "where user_id = #{userId,jdbcType=INTEGER} " +            "and picture_item_id = #{pictureId,jdbcType=INTEGER} limit 1")    @Results({            @Result(column = "id",property = "id"),            @Result(column = "user_id",property = "userId"),            @Result(column = "picture_item_id",property = "pictureItemId")    })    CollectPictureLog findUserCollect(@Param("userId") Integer userId, @Param("pictureId") Integer pictureId);    @Select("select id,user_id,picture_item_id " +            "from collect_picture_log " +            "where user_id = #{userId,jdbcType=INTEGER} " +            "and picture_item_id in (${pictureItemIds})")    @Results({            @Result(column = "id",property = "id"),            @Result(column = "user_id",property = "userId"),            @Result(column = "picture_item_id",property = "pictureItemId")    })    List<CollectPictureLog> findByIds(@Param("userId")Integer userId,@Param("pictureItemIds")String pictureItemIds);}