package com.kucaroom.mypicture.mapper;import com.kucaroom.mypicture.domain.DownLoadPictureLog;import org.apache.ibatis.annotations.Param;import org.apache.ibatis.annotations.Result;import org.apache.ibatis.annotations.Results;import org.apache.ibatis.annotations.Select;public interface DownloadPictureMapp {    @Select("select * from " +            "download_picture_log " +            "where user_id = #{userId,jdbcType=INTEGER} " +            "and picture_item_id = #{pictureItemId,jdbcType=INTEGER} limit 1")    @Results({            @Result(column = "id",property = "id"),            @Result(column = "user_id",property = "userId"),            @Result(column = "picture_item_id",property = "pictureItemId"),            @Result(column = "type",property = "type"),            @Result(column = "number",property = "number")    })    DownLoadPictureLog findByUser(@Param("userId") Integer userId,@Param("pictureItemId") Integer pictureItemId);}