package com.kucaroom.mypicture.mapper;import com.kucaroom.mypicture.domain.PictureItem;import org.apache.ibatis.annotations.Param;import org.apache.ibatis.annotations.Result;import org.apache.ibatis.annotations.Results;import org.apache.ibatis.annotations.Select;import java.util.List;public interface PictureItemMapper {    @Select("select id,picture_id,title from picture_items where id = #{id,jdbcType=INTEGER}")    @Results({            @Result(column = "id",property = "id"),            @Result(column = "picture_id",property = "pictureId"),            @Result(column = "title",property = "title")    })    PictureItem findById(Integer id);    @Select("select id,picture_id,picture,picture_width,picture_height from picture_items where id in (${ids})")    @Results({            @Result(column = "id",property = "id"),            @Result(column = "picture_id",property = "pictureId"),            @Result(column = "picture",property = "picture"),            @Result(column = "picture_width",property = "pictureWidth"),            @Result(column = "picture_height",property = "pictureHeight")    })    List<PictureItem> findByInIds(@Param("ids") String ids);}