package com.kucaroom.mypicture.mapper;import com.kucaroom.mypicture.domain.Picture;import org.apache.ibatis.annotations.Param;import org.apache.ibatis.annotations.Result;import org.apache.ibatis.annotations.Results;import org.apache.ibatis.annotations.Select;import java.util.List;public interface PictureMapper {    /**     * 根据ID获取图集     *     * @param ids     * @return     */    @Select("select id,cover_picture,cover_width,cover_height from pictures where id in (${ids})")    @Results({            @Result(column = "id",property = "id"),            @Result(column = "cover_picture",property = "coverPicture"),            @Result(column = "cover_width",property = "coverWidth"),            @Result(column = "cover_height",property = "coverHeight")    })    public List<Picture> findInIds(@Param("ids")String ids);}