package com.kucaroom.mypicture.mapper;import com.kucaroom.mypicture.domain.ViewPictureLog;import org.apache.ibatis.annotations.Param;import org.apache.ibatis.annotations.Result;import org.apache.ibatis.annotations.Results;import org.apache.ibatis.annotations.Select;public interface ViewPictureLogMapper {    @Select("select * from view_picture_log " +            "where user_id = #{userId,jdbcType=INTEGER} " +            "and picture_id = #{pictureId,jdbcType=INTEGER} " +            "and type = #{type,jdbcType=INTEGER} limit 1")    @Results({            @Result(column = "id",property = "id"),            @Result(column = "user_id",property = "userId"),            @Result(column = "picture_id",property = "pictureId"),            @Result(column = "type",property = "type"),            @Result(column = "number",property = "number")    })    ViewPictureLog findByUserViewLog(@Param("userId")Integer userId,                                     @Param("pictureId")Integer pictureId,                                     @Param("type")Integer type);}