package com.kucaroom.mypicture.service.impl;import com.kucaroom.mypicture.DTO.UserDTO;import com.kucaroom.mypicture.domain.CollectPictureLog;import com.kucaroom.mypicture.domain.PictureItem;import com.kucaroom.mypicture.enums.ResponseEnum;import com.kucaroom.mypicture.exception.ApiException;import com.kucaroom.mypicture.repository.CollectPictureLogRepository;import com.kucaroom.mypicture.service.CollectPictureLogService;import com.kucaroom.mypicture.service.PictureItemService;import com.kucaroom.mypicture.service.UserService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;@Servicepublic class CollectPictureLogServiceImpl implements CollectPictureLogService {    @Autowired    private CollectPictureLogRepository repository;    @Autowired    private UserService userService;    @Autowired    private PictureItemService pictureItemService;    /**     * 收藏图片     *     * @author yezi     * @param userId     * @param pictureItemId     * @return     */    @Override    public CollectPictureLog create(Integer userId, Integer pictureItemId) {        UserDTO userDTO = userService.finById(userId);        if(userDTO == null){            throw new ApiException(ResponseEnum.USER_NOT_EXIST);        }        PictureItem pictureItem = pictureItemService.findById(pictureItemId);        if(pictureItem == null){            throw new ApiException(ResponseEnum.PICTURE_ITEM_NOT_EXIST);        }        CollectPictureLog collectPictureLog = new CollectPictureLog();        collectPictureLog.setUserId(userId);        collectPictureLog.setPictureItemId(pictureItemId);        CollectPictureLog log = repository.save(collectPictureLog);        if(log == null){            throw new ApiException(ResponseEnum.PICTURE_COLLECT_FAIL);        }        return log;    }}