package com.kucaroom.mypicture.util;import com.kucaroom.mypicture.domain.PictureItem;import java.util.HashMap;import java.util.Map;public class PictureInfoUtil {    public static Map<String,Object> setPictureInfo(PictureItem pictureItem){        Map<String,Object> pictureMap = new HashMap<>();        pictureMap.put("url",pictureItem.getPicture());        pictureMap.put("width",pictureItem.getPictureWidth());        pictureMap.put("height",pictureItem.getPictureHeight());        pictureMap.put("id",pictureItem.getId());        return pictureMap;    }}