package com.kucaroom.mypicture.mapper;import com.kucaroom.mypicture.domain.CollectPictureLog;import lombok.extern.slf4j.Slf4j;import org.apache.commons.lang.StringUtils;import org.junit.Assert;import org.junit.Test;import org.junit.runner.RunWith;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.boot.test.context.SpringBootTest;import org.springframework.test.context.junit4.SpringRunner;import java.util.ArrayList;import java.util.List;import java.util.Map;import static org.junit.Assert.*;@Slf4j@SpringBootTest@RunWith(SpringRunner.class)public class CollectPictureMapperTest {    @Autowired    private CollectPictureMapper mapper;    @Test    public void findUserCollect() {        CollectPictureLog pictureLog = mapper.findUserCollect(7612,118);        log.info("结果:{}",pictureLog.toString());        Assert.assertNotNull(pictureLog);    }    @Test    public void findIds(){        List<Integer> ids = new ArrayList<>();        ids.add(115);        ids.add(117);        ids.add(90);        List<CollectPictureLog> list = mapper.findByIds(7612,StringUtils.strip(ids.toString(),"[]"));        log.info("结果：{}",list.toString());        Assert.assertNotNull(list);    }}