package com.kucaroom.mypicture.mapper;import com.kucaroom.mypicture.domain.PictureItem;import lombok.extern.slf4j.Slf4j;import org.apache.commons.lang.StringUtils;import org.junit.Assert;import org.junit.Test;import org.junit.runner.RunWith;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.boot.test.context.SpringBootTest;import org.springframework.test.context.junit4.SpringRunner;import java.util.ArrayList;import java.util.List;import static org.junit.Assert.*;@Slf4j@RunWith(SpringRunner.class)@SpringBootTestpublic class PictureItemMapperTest {    @Autowired    private PictureItemMapper pictureItemMapper;    @Test    public void findById() {        PictureItem pictureItem = pictureItemMapper.findById(88);        log.info("结果：{}",pictureItem.toString());        Assert.assertNotNull(pictureItem);    }    @Test    public void findByInIds() {        List<Integer> ids = new ArrayList<>();        ids.add(90);        ids.add(91);        ids.add(92);        List<PictureItem> list = pictureItemMapper.findByInIds(StringUtils.strip(ids.toString(),"[]"));        log.info("结果:{}",list.toString());    }}