package com.kucaroom.mypicture.repository;import com.kucaroom.mypicture.domain.DownLoadPictureLog;import org.junit.Assert;import org.junit.Test;import org.junit.runner.RunWith;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.boot.test.context.SpringBootTest;import org.springframework.test.context.junit4.SpringRunner;@SpringBootTest@RunWith(SpringRunner.class)public class DownLoadPictureLogRepositoryTest {    @Autowired    private DownLoadPictureLogRepository repository;    @Test    public void create(){        DownLoadPictureLog log = new DownLoadPictureLog();        log.setPictureItemId(100);        log.setUserId(7607);        DownLoadPictureLog result = repository.save(log);        Assert.assertNotNull(result);        Assert.assertEquals(log.getUserId(),result.getUserId());    }}