package com.kucaroom.mypicture.service.impl;import com.kucaroom.mypicture.domain.CollectPictureLog;import com.kucaroom.mypicture.domain.ViewPictureLog;import com.kucaroom.mypicture.service.CollectPictureLogService;import lombok.extern.slf4j.Slf4j;import org.junit.Assert;import org.junit.Test;import org.junit.runner.RunWith;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.boot.test.context.SpringBootTest;import org.springframework.data.domain.Page;import org.springframework.data.domain.PageRequest;import org.springframework.test.context.junit4.SpringRunner;import static org.junit.Assert.*;@RunWith(SpringRunner.class)@SpringBootTest@Slf4jpublic class CollectPictureLogServiceImplTest {    @Autowired    private CollectPictureLogService service;    @Test    public void create() {        Integer pictureItemId = 100;        Integer userId = 7607;        CollectPictureLog log = service.create(userId,pictureItemId);        Assert.assertNotNull(log);        Assert.assertEquals(userId,log.getUserId());    }    @Test    public void list() {        PageRequest pageRequest = new PageRequest(0,10);        Page<CollectPictureLog> list = service.list(7607,pageRequest);        log.info("内容：{}",list.getContent().toString());        Assert.assertNotNull(list);    }}